CREATE SCHEMA IF NOT EXISTS "CLAIM";
SET SCHEMA "CLAIM";

DROP TABLE IF EXISTS "CLAIM"."MEMBERS";
CREATE TABLE IF NOT EXISTS "CLAIM"."MEMBERS" (
  "MEMBER_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "FIRST_NAME" VARCHAR(15) NOT NULL,
  "LAST_NAME" VARCHAR(15) NOT NULL,
  "AGE" INT NOT NULL,
  "GENDER" VARCHAR(7) NOT NULL,
  "DOB" DATE NOT NULL,
  "ADDRESS" VARCHAR(30) NOT NULL,
  PRIMARY KEY ("MEMBER_ID")
);

DROP TABLE IF EXISTS "CLAIM"."POLICY";
CREATE TABLE IF NOT EXISTS "CLAIM"."POLICY" (
  "POLICY_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "POLICY_NAME" VARCHAR(25) NOT NULL,
  "BENEFIT_ID" VARCHAR(10) NOT NULL,
  "BENEFITS" VARCHAR(30) NOT NULL,
  "PREMIUM_AMT" BIGINT NOT NULL,
  "TENURE" BIGINT NOT NULL,
  "CLAIM_AMT" BIGINT NOT NULL,
  PRIMARY KEY ("POLICY_ID")
);

DROP TABLE IF EXISTS "CLAIM"."CLAIMS";
CREATE TABLE IF NOT EXISTS "CLAIM"."CLAIMS" (
  "CLAIM_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "CLAIM_STATUS" VARCHAR(25) NOT NULL,
  "MEMBER_ID" VARCHAR(25) NOT NULL,
  "POLICY_ID" VARCHAR(25) NOT NULL,
  "HOSPITAL_ID" VARCHAR(25) NOT NULL,
  "BENEFIT_ID" VARCHAR(25) NOT NULL,
  "TOTAL_BILL" BIGINT NOT NULL,
  "CLAIMED_AMT" BIGINT NOT NULL,
  PRIMARY KEY ("CLAIM_ID")
);

DROP TABLE IF EXISTS "CLAIM"."MEMBER_POLICY";
CREATE TABLE IF NOT EXISTS "CLAIM"."MEMBER_POLICY" (
  "MEMBER_POLICY_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "MEMBER_ID" VARCHAR(10) NOT NULL,
  "POLICY_ID" VARCHAR(10) NOT NULL,
  "SUBSCRIPTION_DATE" DATE NOT NULL,
  "LAST_PREMIUM_DATE" DATE NOT NULL,
  "PREMIUM_AMT_DUE" BIGINT NOT NULL,
  "IS_LATE" BOOLEAN NOT NULL,
  "DUE_DATE" DATE NOT NULL,
  PRIMARY KEY ("MEMBER_POLICY_ID")
);

DROP TABLE IF EXISTS "CLAIM"."MEMBER_PREMIUM";
CREATE TABLE IF NOT EXISTS "CLAIM"."MEMBER_PREMIUM" (
  "MEMBER_PREMIUM_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "MEMBER_ID" VARCHAR(10) NOT NULL,
  "POLICY_ID" VARCHAR(10) NOT NULL,
  "LAST_PREMIUM_DATE" DATE NOT NULL,
  "PREMIUM_AMT_DUE" BIGINT NOT NULL,
  PRIMARY KEY ("MEMBER_PREMIUM_ID")
);

DROP TABLE IF EXISTS "CLAIM"."MEMBER_BENEFIT";
CREATE TABLE IF NOT EXISTS "CLAIM"."MEMBER_BENEFIT" (
  "MEMBER_BENEFIT_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "MEMBER_ID" VARCHAR(10) NOT NULL,
  "POLICY_ID" VARCHAR(10) NOT NULL,
  "BENEFIT_ID" VARCHAR(10) NOT NULL,
  "BENEFITS" VARCHAR(30) NOT NULL,
  "CLAIM_AMT" BIGINT NOT NULL,
  PRIMARY KEY ("MEMBER_BENEFIT_ID")
);

DROP TABLE IF EXISTS "CLAIM"."PROVIDER_POLICY";
CREATE TABLE IF NOT EXISTS "CLAIM"."PROVIDER_POLICY" (
  "PROVIDER_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
  "PROVIDER_NAME" VARCHAR(25) NOT NULL,
  "POLICY_ID" VARCHAR(10) NOT NULL,
  "POLICY_NAME" VARCHAR(25) NOT NULL,
  "HOSPITAL_ID" VARCHAR(10) NOT NULL,
  "HOSPITAL_NAME" VARCHAR(25) NOT NULL,
  "LOCATION" VARCHAR(10) NOT NULL,
  "BENEFIT_ID" VARCHAR(10) NOT NULL,
  "BENEFITS" VARCHAR(30) NOT NULL,
  "PREMIUM_AMT" BIGINT NOT NULL,
  "TENURE" BIGINT NOT NULL,
  "CLAIM_AMT" BIGINT NOT NULL,
  PRIMARY KEY ("PROVIDER_ID")
);
